import React from 'react';
import { ShoppingBag } from 'lucide-react';

const Logo = ({ size = 'md', variant = 'full', className = '' }) => {
  const sizes = {
    sm: {
      text: 'text-lg',
      icon: 'h-5 w-5',
      spacing: 'space-x-1'
    },
    md: {
      text: 'text-2xl',
      icon: 'h-6 w-6',
      spacing: 'space-x-2'
    },
    lg: {
      text: 'text-3xl',
      icon: 'h-8 w-8',
      spacing: 'space-x-3'
    },
    xl: {
      text: 'text-4xl',
      icon: 'h-10 w-10',
      spacing: 'space-x-3'
    }
  };

  const sizeClasses = sizes[size];

  if (variant === 'icon') {
    return (
      <div className={`flex items-center justify-center ${className}`}>
        <div className="relative">
          <ShoppingBag className={`${sizeClasses.icon} text-indigo-600`} />
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-orange-500 rounded-full"></div>
        </div>
      </div>
    );
  }

  return (
    <div className={`flex items-center ${sizeClasses.spacing} ${className}`}>
      <div className="relative">
        <ShoppingBag className={`${sizeClasses.icon} text-indigo-600`} />
        <div className="absolute -top-1 -right-1 w-2 h-2 bg-orange-500 rounded-full animate-pulse"></div>
      </div>
      <span className={`${sizeClasses.text} font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent`}>
        NexaShop
      </span>
    </div>
  );
};

export default Logo;
